import React, { Component, createRef } from "react";

class Video extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.localVideo = createRef();
    this.remoteVideo = createRef();
  }

  handleConnection = e => {
    if (e.candidate) {
      const newIceCandidate = new RTCIceCandidate(e.candidate);
      const otherPeer =
        e.target === this.localPeerConnection
          ? this.remotePeerConnection
          : this.localPeerConnection;

      otherPeer
        .addIceCandidate(newIceCandidate)
        .then(() => {
          console.log(e.target, "add iceCandidate success");
        })
        .catch(error => {
          console.error(e.target, error);
        });
    }
  };

  _onStartVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true
      })
      .then(mediaStream => {
        this.localVideo.current.srcObject = mediaStream;
        this.localPeerConnection.addStream(mediaStream);
      })
      .catch(error => console.error(error));
  };

  _onCallButton = () => {
    this.localPeerConnection = new RTCPeerConnection(null);
    this.remotePeerConnection = new RTCPeerConnection(null);

    // local peerconnection
    this.localPeerConnection.onicecandidate = this.handleConnection;
    this.localPeerConnection.oniceconnectionstatechange = e => {
      console.log(e);
    };
    this.localPeerConnection.onaddstream = e => {
      console.log(e);
      this.remoteVideo.current.srcObject = e.stream;
    };

    // remote connection
    this.remotePeerConnection.onicecandidate = this.handleConnection;
    this.remotePeerConnection.oniceconnectionstatechange = e => {
      console.log(e);
    };

    console.log("offer");
    this.localPeerConnection
      .createOffer({ offerToReceiveVideo: 1 })
      .then(sdp => {
        this.localPeerConnection
          .setLocalDescription(sdp)
          .then(() => {
            console.log("set local description succees");
          })
          .catch(error => {
            console.error(error);
          });
      });
  };

  componentDidMount() {}

  render() {
    return (
      <div>
        <video ref={this.localVideo} autoPlay playsInline />
        <video ref={this.remoteVideo} autoPlay playsInline />
        <button onClick={this._onStartVideo}>시작</button>
        <button onClick={this._onCallButton}>콜 버튼</button>
      </div>
    );
  }
}

export default Video;
