import React, { Component, createRef } from "react";
import { getUserMediaContraints, CustomTrace } from "../../config/RTCSettings";

class RemoteVideo extends Component {
  constructor(props) {
    super(props);

    this.remoteVideo = createRef(); // video tag ref
    this.remoteStream = createRef(); // local stream referencing variable
    this.state = {};
  }

  // handle error
  handleLocalMediaStreamError = error => {
    console.error(error);
  };

  // handle get user media stream
  getLocalMediaStream = mediaStream => {
    this.localVideo.current.srcObject = mediaStream;
    this.localStream = mediaStream;

    CustomTrace("start receiving local media stream!");
    this.props.onChangeCallButtonDisable(false);
    this.props.onChangeStartButtonDisable(true);
  };

  _onOpenCamera = () => {
    navigator.mediaDevices
      .getUserMedia(getUserMediaContraints)
      .then(getLocalMediaStream)
      .catch(handleLocalMediaStreamError);
  };

  render() {
    return (
      <div>
        <video autoPlay playsInline ref={this.remoteVideo} />
        <button
          disabled={this.props.startButtonDisable}
          onClick={this._onOpenCamera}
        >
          콜 버튼
        </button>
      </div>
    );
  }
}

export default RemoteVideo;
