import React, { Component } from "react";
import Video from "./Video";
import { getUserMediaContraints } from "../../config/RTCSettings";

class Container extends Component {
  constructor(props) {
    super(props);

    this.state = {
      callButtonDisable: true,
      startButtonDisable: false
    };
  }
  // 시작
  _onStartClickButton = () => {
    navigator.mediaDevices
      .getUserMedia(getUserMediaContraints)
      .then(handleGetLocalMediaStream)
      .catch(handleErrorGetLocalMediaStream);
  };

  render() {
    return (
      <div>
        <div className="videos">
          <video />
          <video />
        </div>
        <div className="buttons">
          <button
            disabled={this.state.startButtonDisable}
            onClick={this._onStartClickButton}
          >
            시작 버튼
          </button>
          <button disabled={this.state.callButtonDisable}>콜 버튼</button>
        </div>
      </div>
    );
  }

  // error handling
  handleErrorGetLocalMediaStream = error => {
    console.error(error);
  };
}

export default Container;
