import React, { createRef, Component } from "react";
import "./style.scss";
class index extends Component {
  constructor(props) {
    super(props);
    this.localVideo = createRef();
    this.remoteVideo = createRef();
    this.localStream = createRef();
    this.state = {
      callButtonDisabled: true
    };
  }

  handleConnection(event) {
    const peerConnection = event.target;
    const iceCandidate = event.candidate;

    if (iceCandidate) {
      const newIceCandidate = new RTCIceCandidate(iceCandidate);
      const otherPeer = getOtherPeer(peerConnection);

      otherPeer
        .addIceCandidate(newIceCandidate)
        .then(() => {
          handleConnectionSuccess(peerConnection);
        })
        .catch(error => {
          handleConnectionFailure(peerConnection, error);
        });

      trace(
        `${getPeerName(peerConnection)} ICE candidate:\n` +
          `${event.candidate.candidate}.`
      );
    }
  }

  mediaStreamConstraints = {
    video: true,
    audio: true
  };

  gotLocalMediaStream = mediaStream => {
    this.localStream = mediaStream;
    this.localVideo.current.srcObject = mediaStream;

    trace("Received local stream.");
    this.setState({ callButtonDisabled: false });
  };

  handleLocalMediaStreamError(error) {
    console.log("navigator.getUserMedia error: ", error);
  }

  onClickButton = () => {
    navigator.mediaDevices
      .getUserMedia(this.mediaStreamConstraints)
      .then(this.gotLocalMediaStream)
      .catch(this.handleLocalMediaStreamError);
  };

  onClickStop = () => {
    this.localStream.getVideoTracks()[0].stop();
  };

  onClickCall = () => {};

  componentDidMount() {
    let localPeerConnection = new RTCPeerConnection(servers);
    localPeerConnection.addEventListener("icecandidate", handleConnection);
    localPeerConnection.addEventListener(
      "iceconnectionstatechange",
      handleConnectionChange
    );
  }

  render() {
    return (
      <div>
        <h1>Realtime communication with WebRTC</h1>
        <video id="local-video" autoPlay playsInline ref={this.localVideo} />
        <video id="remote-video" autoPlay playsInline ref={this.remoteVideo} />
        <div className="button">
          <button onClick={this.onClickButton}>내 카메라 키기</button>
          <button onClick={this.onClickStop}>종료</button>
          <button
            disabled={this.state.callButtonDisabled}
            onClick={this.onClickCall}
          >
            불러오기
          </button>
        </div>
      </div>
    );
  }
}

export default index;
