import React, { Component, createRef } from "react";
import { getUserMediaContraints, CustomTrace } from "../../config/RTCSettings";

class RemoteVideo extends Component {
  constructor(props) {
    super(props);

    this.remoteVideo = createRef(); // video tag ref
    this.remoteStream = createRef(); // local stream referencing variable
    this.state = {};
  }

  _onStartCall = () => {
    this.props.onChangeCallButtonDisable(true);
  };

  render() {
    return (
      <div>
        <video autoPlay playsInline ref={this.remoteVideo} />
        <button
          disabled={this.props.callButtonDisable}
          onClick={this._onStartCall}
        >
          콜 버튼
        </button>
      </div>
    );
  }
}

export default RemoteVideo;
