import React, { Component, createRef } from "react";

class Video extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.localVideo = createRef();
    this.remoteVideo = createRef();
  }

  handleConnection = e => {
    if (e.candidate) {
      const newIceCandidate = new RTCIceCandidate(e.candidate);
      const otherPeer =
        e.target === this.localPeerConnection
          ? this.remotePeerConnection
          : this.localPeerConnection;

      otherPeer
        .addIceCandidate(newIceCandidate)
        .then(() => {
          console.log(e.target, "add iceCandidate success");
        })
        .catch(error => {
          console.error(e.target, error);
        });
    }
  };

  _onStartVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true
      })
      .then(mediaStream => {
        this.localVideo.current.srcObject = mediaStream;
        mediaStream.getTracks(track => {
          this.localPeerConnection.addTrack(track, mediaStream);
        });
      })
      .catch(error => console.error(error));
  };

  _onCallButton = () => {
    console.log("offer");
    this.pc.createOffer({ offerToReceiveVideo: 1 }).then(sdp => {
      this.localPeerConnection
        .setLocalDescription(sdp)
        .then(() => {
          console.log("set local description succees");
        })
        .catch(error => {
          console.error(error);
        });
    });
  };

  componentDidMount() {
    this.pc = new RTCPeerConnection(null);
    this.pc = new RTCPeerConnection(null);

    // local peerconnection
    this.pc.onicecandidate = this.handleConnection;
    this.pc.oniceconnectionstatechange = e => {
      console.log(e);
    };
    this.pc.ontrack = e => {
      console.log(e);
      this.remoteVideo.current.srcObject = e.streams[0];
    };
  }

  render() {
    return (
      <div>
        <video ref={this.localVideo} autoPlay playsInline />
        <video ref={this.remoteVideo} autoPlay playsInline />
        <button onClick={this._onStartVideo}>시작</button>
        <button onClick={this._onCallButton}>콜 버튼</button>
      </div>
    );
  }
}

export default Video;
