import React, { Component, createRef } from "react";

let localPeerConnection;
let remotePeerConnection;

class Video extends Component {
  constructor(props) {
    super(props);

    this.state = {};
    this.localVideo = createRef();
    this.remoteVideo = createRef();
  }

  _onStartVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true
      })
      .then(mediaStream => {
        this.localVideo.current.srcObject = mediaStream;
        this.pc.addstream = mediaStream;
      })
      .catch(error => console.error(error));
  };

  _onCallButton = () => {
    localPeerConnection = new RTCPeerConnection(null);
    localPeerConnection.onicecandidate = () => {};

    createOffer = () => {
      console.log("offer");
      this.pc.createOffer({ offerToReceiveVideo: 1 }).then(sdp => {
        this.pc.setLocalDescription(sdp);
      });
    };
  };

  componentDidMount() {
    this.pc = new RTCPeerConnection(null);
    this.pc.onicecandidate = e => {
      if (e.candidate) {
        console.log(JSON.stringify(e.candidate));
      }
    };
    this.pc.oniceconnectionstatechange = e => {
      console.log(e);
    };
    this.pc.onaddstream = e => {
      this.remoteVideo.current.srcObject = e.stream;
    };
  }

  render() {
    return (
      <div>
        <video ref={this.localVideo} autoPlay playsInline />
        <video ref={this.remoteVideo} autoPlay playsInline />
        <button onClick={this._onStartVideo}>시작</button>
        <button onClick={this._onCallButton}>콜 버튼</button>
      </div>
    );
  }
}

export default Video;
