import React, { Component, createRef } from "react";
import Video from "./Video";
import { getUserMediaContraints, CustomTrace } from "../../config/RTCSettings";

class Container extends Component {
  constructor(props) {
    super(props);

    this.localVideo = createRef();
    this.localStream = createRef();
    this.localPeerConnection = createRef();
    this.remotePeerConnection = createRef();

    this.state = {
      callButtonDisable: true,
      startButtonDisable: false,
      stopButtonDisable: true
    };
  }
  // 시작
  _onStartClickButton = () => {
    navigator.mediaDevices
      .getUserMedia(getUserMediaContraints)
      .then(this.handleGetLocalMediaStream)
      .catch(this.handleErrorGetLocalMediaStream);
  };

  handleGetLocalMediaStream = mediaStream => {
    this.localVideo.current.srcObject = mediaStream;
    this.localStream = mediaStream;

    CustomTrace("get local media stream!");
    this.setState({
      startButtonDisable: true,
      callButtonDisable: false,
      stopButtonDisable: false
    });
  };

  _onClickStopButton = () => {
    this.setState({
      startButtonDisable: false,
      stopButtonDisable: true
    });
    this.localStream.getVideoTracks()[0].stop();
    this.localStream.getAudioTracks()[0].stop();
  };

  // 콜 버튼 눌렀을 때
  _onClickCallButton = () => {
    this.setState({ callButtonDisable: true });

    CustomTrace("Starting call");
    const videoTracks = this.localStream.getVideoTracks();
    const audioTracks = this.localStream.getAudioTracks();

    if (videoTracks.length > 0) {
      CustomTrace(`Using video device: ${videoTracks[0].label}.`);
    }
    if (audioTracks.length > 0) {
      CustomTrace(`Using audio device: ${audioTracks[0].label}.`);
    }

    const servers = null; // Allows for RTC server configuration.

    // Create peer connections and add behavior.
    this.localPeerConnection = new RTCPeerConnection();
    CustomTrace("Created local peer connection object localPeerConnection.");

    this.localPeerConnection.addEventListener(
      "icecandidate",
      this.handleConnection
    );
    this.localPeerConnection.addEventListener(
      "iceconnectionstatechange",
      this.handleConnectionChange
    );

    this.remotePeerConnection = new RTCPeerConnection();
    CustomTrace("Created remote peer connection object remotePeerConnection.");

    this.remotePeerConnection.addEventListener(
      "icecandidate",
      this.handleConnection
    );
    this.remotePeerConnection.addEventListener(
      "iceconnectionstatechange",
      this.handleConnectionChange
    );

    // Add local stream to connection and create offer to connect.
    this.localPeerConnection.addStream(localStream);
    trace("Added local stream to localPeerConnection.");

    trace("localPeerConnection createOffer start.");
    this.localPeerConnection
      .createOffer(this.offerOptions)
      .then(this.createdOffer)
      .catch(this.setSessionDescriptionError);
  };

  handleConnection(event) {
    const peerConnection = event.target;
    const iceCandidate = event.candidate;

    if (iceCandidate) {
      const newIceCandidate = new RTCIceCandidate(iceCandidate);
      const otherPeer = getOtherPeer(peerConnection);

      otherPeer
        .addIceCandidate(newIceCandidate)
        .then(() => {
          handleConnectionSuccess(peerConnection);
        })
        .catch(error => {
          handleConnectionFailure(peerConnection, error);
        });

      CustomTrace(
        `${getPeerName(peerConnection)} ICE candidate:\n` +
          `${event.candidate.candidate}.`
      );
    }
  }

  // Logs changes to the connection state.
  handleConnectionChange(event) {
    const peerConnection = event.target;
    console.log("ICE state change event: ", event);
    trace(
      `${getPeerName(peerConnection)} ICE state: ` +
        `${peerConnection.iceConnectionState}.`
    );
  }

  render() {
    return (
      <div>
        <div className="videos">
          <video autoPlay playsInline ref={this.localVideo} />
          <video autoPlay playsInline />
        </div>
        <div className="buttons">
          <button
            disabled={this.state.startButtonDisable}
            onClick={this._onStartClickButton}
          >
            시작 버튼
          </button>
          <button
            disabled={this.state.stopButtonDisable}
            onClick={this._onClickStopButton}
          >
            종료
          </button>
          <button
            disabled={this.state.callButtonDisable}
            onClick={this._onClickCallButton}
          >
            콜 버튼
          </button>
        </div>
      </div>
    );
  }

  // error handling
  handleErrorGetLocalMediaStream = error => {
    console.error(error);
  };
}

export default Container;
