import React, { Component, createRef } from "react";
import { getUserMediaContraints } from "../../config/RTCSettings";

class LocalVideo extends Component {
  constructor(props) {
    super(props);

    this.localVideo = createRef(); // video tag ref
    this.localStream = createRef(); // local stream referencing variable
    this.state = {};
  }

  // handle error
  handleLocalMediaStreamError = error => {
    console.error(error);
  };

  // handle get user media stream
  getLocalMediaStream = mediaStream => {
    this.localVideo.current.srcObject = mediaStream;
  };

  _onOpenCamera = () => {
    navigator.mediaDevices
      .getUserMedia(getUserMediaContraints)
      .then(getLocalMediaStream)
      .catch(handleLocalMediaStreamError);
  };

  render() {
    return (
      <div>
        <video autoPlay playsInline ref={this.localVideo} />
        <button onClick={this._onOpenCamera}>카메라 오픈</button>
      </div>
    );
  }
}

export default LocalVideo;
