import React, { Component, createRef } from "react";

class Video extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.localVideo = createRef();
    this.remoteVideo = createRef();
  }

  _onStartVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true
      })
      .then(mediaStream => {
        console.log(mediaStream);
        this.localVideo.current.srcObject = mediaStream;
        mediaStream
          .getTracks()
          .reduce(track => this.localPeerConnection.addTrack(track));
      })
      .catch(error => console.error(error));
  };

  _onCallButton = () => {
    console.log("offer start");
    this.localPeerConnection
      .createOffer({ offerToReceiveVideo: 1 })
      .then(sdp => {
        this.localPeerConnection
          .setLocalDescription(sdp)
          .then(() =>
            console.log("answer: set local description in local success")
          )
          .catch(err => console.error(err));

        this.remotePeerConnection
          .setRemoteDescription(sdp)
          .then(() =>
            console.log("answer: set remote description in remote success")
          )
          .catch(err => console.error(err));
        console.log("answer start");

        this.remotePeerConnection.createAnswer().then(sdp => {
          this.remotePeerConnection
            .setLocalDescription(sdp)
            .then(() =>
              console.log("answer: set local description in remote success")
            )
            .catch(err => console.error(err));

          this.localPeerConnection
            .setRemoteDescription(sdp)
            .then(() => {
              console.log("answer: set remote description in local success");
            })
            .catch(err => console.error(err));
        });
      });
  };

  componentDidMount() {
    this.localPeerConnection = new RTCPeerConnection(null);
    this.remotePeerConnection = new RTCPeerConnection(null);

    // local peerconnection
    this.localPeerConnection.onicecandidate = e => {
      if (e.candidate) {
        const newIceCandidate = new RTCIceCandidate(e.candidate);
        this.localPeerConnection
          .addIceCandidate(newIceCandidate)
          .then(() => {
            console.log("add iceCandidate success");
          })
          .catch(error => {
            console.error(error);
          });
      }
    };
    this.localPeerConnection.oniceconnectionstatechange = e => {
      console.log(e);
    };
    // remote peerconnection
    this.remotePeerConnection.onicecandidate = e => {
      if (e.candidate) {
        const newIceCandidate = new RTCIceCandidate(e.candidate);
        this.remotePeerConnection
          .addIceCandidate(newIceCandidate)
          .then(() => {
            console.log("add iceCandidate success");
          })
          .catch(error => {
            console.error(error);
          });
      }
    };
    this.remotePeerConnection.oniceconnectionstatechange = e => {
      console.log(e);
    };

    this.localPeerConnection.ontrack = e => {
      console.log(e);
      this.remoteVideo.current.srcObject = e.streams[0];
    };
  }

  render() {
    return (
      <div>
        <video
          style={{ backgroundColor: "black", width: 200, height: 200 }}
          ref={this.localVideo}
          autoPlay
          playsInline
        />
        <video
          style={{
            backgroundColor: "black",
            width: 200,
            height: 200,
            marginLeft: 10
          }}
          ref={this.remoteVideo}
          autoPlay
          playsInline
        />
        <button onClick={this._onStartVideo}>시작</button>
        <button onClick={this._onCallButton}>콜 버튼</button>
      </div>
    );
  }
}

export default Video;
