import React, { Component, createRef } from "react";
import io from "socket.io-client";

class Video extends Component {
  constructor(props) {
    super(props);

    this.state = {};
    this.localVideo = createRef();
    this.remoteVideo = createRef();
  }

  componentDidMount() {
    this.socket = io("http://localhost:8080/webrtcPeer", {
      path: "/webrtc"
    });

    this.socket.on("connection-success", success => {
      console.log(success);
    });

    this.socket.on("offerOrAnswer", sdp => {
      this.pc.setRemoteDescription(new RTCSessionDescription(sdp));
    });

    const pc_config = {
      urls: "stun:stun.l.google.com:19302"
    };

    this.pc = new RTCPeerConnection(pc_config);
    this.pc.onicecandidate = e => {
      if (e.candidate) {
      }
    };

    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true
      })
      .then(mediaStream => {
        this.localVideo.current.srcObject = mediaStream;
        mediaStream.getTracks().reduce(() => {});
      })
      .catch(err => console.error(err));
  }

  _onCallButton = () => {};

  render() {
    return (
      <div>
        <video ref={this.localVideo} autoPlay playsInline />
        <video ref={this.remoteVideo} autoPlay playsInline />
        <button onClick={this._onCallButton}>call button</button>
      </div>
    );
  }
}

export default Video;
