import React, { Component } from "react";
import LocalVideo from "./LocalVideo";
import RemoteVideo from "./RemoteVideo";

class Container extends Component {
  constructor(props) {
    super(props);

    this.state = {
      callButtonDisable: true,
      startButtonDisable: false
    };
  }
  onChangeCallButtonDisable = bool => {
    this.setState({ callButtonDisable: bool });
  };

  onChangeStartButtonDisable = bool => {
    this.setState({ startButtonDisable: bool });
  };

  render() {
    return (
      <div>
        <LocalVideo
          startButtonDisable={this.state.startButtonDisable}
          onChangeCallButtonDisable={this.onChangeCallButtonDisable}
          onChangeStartButtonDisable={this.onChangeStartButtonDisable}
        />

        <RemoteVideo callButtonDisable={this.state.callButtonDisable} />
      </div>
    );
  }
}

export default Container;
